-- Create "payments" table
CREATE TABLE "payments" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "out_trade_no" character varying NOT NULL, "transaction_id" character varying NULL, "open_id" character varying NULL, "channel" character varying NOT NULL, "platform" character varying NOT NULL, "amount" bigint NOT NULL, "currency" character varying NOT NULL, "description" character varying NOT NULL, "status" character varying NOT NULL, "paid_at" timestamptz NULL, "user_id" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "payments_users_payments" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "payment_out_trade_no" to table: "payments"
CREATE UNIQUE INDEX "payment_out_trade_no" ON "payments" ("out_trade_no");
-- Create "wechat_open_ids" table
CREATE TABLE "wechat_open_ids" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "deleted_at" timestamptz NULL, "open_id" character varying NOT NULL, "platform" character varying NOT NULL, "union_id" character varying NULL, "user_id" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "wechat_open_ids_users_open_ids" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "wechatopenid_open_id_platform" to table: "wechat_open_ids"
CREATE UNIQUE INDEX "wechatopenid_open_id_platform" ON "wechat_open_ids" ("open_id", "platform");
-- Create index "wechatopenid_user_id" to table: "wechat_open_ids"
CREATE INDEX "wechatopenid_user_id" ON "wechat_open_ids" ("user_id");
